import React, {Component} from 'react';
import TextField from 'material-ui/TextField';
import Paper from 'material-ui/Paper';
import RaisedButton from 'material-ui/RaisedButton';
import KissanNumber from './constants/kissanNumbers';
import { ToastContainer, toast } from 'react-toastify';
import web3 from './web3';
import contract from './contract';
import 'react-toastify/dist/ReactToastify.css';
import _ from 'lodash';
class Login extends Component {
    constructor(props) {
        super(props);
        this.state = {
            kissanNum: ''
        }
    }
    notify = async () => {
        // try{
        //     const accounts = await web3.eth.getAccounts();
        //     const sen = await contract.methods.checkUser(1999).send({from: accounts[0]});
        //   const manager = await contract.events.CallForRegister({fromBlock: 0, toBlock: 'latest'},(err, evet) => console.log(evet));

        //   // this.setState({
        //   //   manager: manager
        //   // });
        // } catch(err) {
        //   console.log(err);
        // }
        console.log(_.filter(KissanNumber,this.state.kissanNum));
        toast.error("Warning Notification !");
    }
    getKissanNumber = (e) => {
        this.setState({
            kissanNum: e.target.value
        });
    }
    render() {
        return (
            <Paper className="loginContianer" zDepth={1}>
                <h3>Please login </h3>
            <TextField
            onChange={this.getKissanNumber}
            hintText="Enter your Kissan Number"
          />
              <RaisedButton onClick={this.notify} label="Submit" primary={true} />
              <ToastContainer />

          </Paper>
        )
    }
};
export default Login;
