import React from 'react';
import TextField from 'material-ui/TextField';
import Paper from 'material-ui/Paper';
import RaisedButton from 'material-ui/RaisedButton';
import SelectField from 'material-ui/SelectField';
import MenuItem from 'material-ui/MenuItem';
class BuyPolicy extends React.Component {
    constructor(props) {
        super(props);
        state = {
            value: 1,
        };
    }
    handleChange = (event, index, value) => this.setState({ value });

    render() {
        return (
            <Paper className="loginContianer" zDepth={1}>
                <h3>Buy crop damage policy</h3>
                <div>
                    <SelectField
                        floatingLabelText="Frequency"
                        value={this.state.value}
                        onChange={this.handleChange}
                    >
                        <MenuItem value={1} primaryText="Never" />
                        <MenuItem value={2} primaryText="Every Night" />
                        <MenuItem value={3} primaryText="Weeknights" />
                        <MenuItem value={4} primaryText="Weekends" />
                        <MenuItem value={5} primaryText="Weekly" />
                    </SelectField>
                    <TextField
                        name="name"
                        onChange={this.getUserDetails}
                        hintText="Enter your Name"
                    />
                    <TextField
                        name="location"
                        onChange={this.getUserDetails}
                        hintText="enter your Location"
                    />
                    <TextField
                        name="kissanNumber"
                        onChange={this.getUserDetails}
                        hintText="Enter your kissan number"
                    />
                    <RaisedButton onClick={this.register} label="Submit" primary={true} />
                </div>

                <ToastContainer />

            </Paper>
        );
    }
}
export default BuyPolicy;
