import React from 'react';
import TextField from 'material-ui/TextField';
import Paper from 'material-ui/Paper';
import RaisedButton from 'material-ui/RaisedButton';
import SelectField from 'material-ui/SelectField';
import MenuItem from 'material-ui/MenuItem';
import { ToastContainer, toast } from 'react-toastify';
import 'react-toastify/dist/ReactToastify.css';
import web3 from './web3';
import contract from './contract';
class ClaimPolicy extends React.Component {
    constructor(props) {
        super(props);
    }
    render() {
        return (
            <Paper className="loginContianer" zDepth={1}>
                <h3>Claim for crop damage</h3>
                <div>
                    <SelectField
                        floatingLabelText="Select Commodity"
                        value={this.state.value}
                        onChange={this.handleChange}
                    >
                        <MenuItem value={'Flood'} primaryText="Flood" />
                        <MenuItem value={'Drought'} primaryText="Drought" />
                        <MenuItem value={'rain'} primaryText="Heavy Rain" />
                        <MenuItem value={'snow'} primaryText="Heavy Snow Fall" />
                    </SelectField>
                    <TextField
                        name="acers"
                        type="number"
                        onChange={this.getUserDetails}
                        hintText="Enter number of acers"
                    />
                    <TextField
                        name="location"
                        onChange={this.getUserDetails}
                        hintText="enter your Location"
                    />
                    <RaisedButton onClick={this.calculate} label="Calculate Premium" primary={true} />
                </div>
                <ToastContainer />
                <h4>Total Premium: {this.state.premiumCost}</h4>
                <RaisedButton onClick={this.payPremium} label="Pay" primary={true} />
            </Paper>
        );
    }
}
export default ClaimPolicy;
